sys_script.script:
(function executeRule(current, previous) {

    try {
        var r = new sn_ws.RESTMessageV2('x_654430_iplookup.IPInfo', 'GetIPInfo');

        // ONLY parameter ipinfo expects
        r.setStringParameterNoEscape('ipaddress', current.ip_address);

        var response = r.execute();
        var httpStatus = response.getStatusCode();
        var responseBody = response.getBody();

        gs.info('HTTP Status: ' + httpStatus);
        gs.info('Response Body: ' + responseBody);

        if (httpStatus == 200 && responseBody && responseBody.trim().startsWith('{')) {
            var responseObj = JSON.parse(responseBody);
			gs.info("response city --- "+responseObj.city);
            current.city = responseObj.city; // city comes from response
			current.update();
        } else {
            gs.error('IPInfo failed. Status: ' + httpStatus);
        }

    } catch (ex) {
        gs.error('Exception: ' + ex.message);
    }

})(current, previous);
